mapper.select:
select *
from mybatis.comments
where superior_comment_id = 0
  and article_id = #{articleID}
  and (is_delete = 0 or is_delete is null) ;

select *
from comments
where find_in_set(id, getChildrenComment(#{comment_id}));

mapper.update:
update mybatis.comments
set is_delete = 1
where id = #{commentId};
